import React, {useState} from 'react'

const Blog = ({blog}) => {
  const [showDetails, SETshowDetails] = useState(false)

  const handleToggle = () => {
    SETshowDetails(!showDetails)
  }

  return (
  <div>
    {blog.title} {blog.author}
    <button onClick={handleToggle}>{showDetails ? 'hide' : 'view'}</button>
    {showDetails &&  (
      <ul>
        <li>{blog.url}</li>
        <li>{blog.likes}</li>
      </ul>
    )}
  </div>  
)}

export default Blog
