import React from 'react';
import ReactDOM from 'react-dom'
import { createStore } from 'redux'
import reducer from './reducer'

const store = createStore(reducer)

store.subscribe(() => { // <-- tulostaa jokaisen storen muutoksen konsoliin.
  const storeNow = store.getState()
  console.log(storeNow)
})

const App = () => {
  const good = () => {
    store.dispatch({
      type: 'GOOD'
    })
    const ok = () => {
      store.dispatch({
        type: 'OK'
      })
    }
    const bad = () => {
      store.dispatch({
        type: 'BAD'
      })
    }
    const zero = () => {
      store.dispatch({
        type: 'ZERO'
      })
    }
  }

  return (
    <div>
      <button onClick={good}>good</button> 
      <button>neutral</button> 
      <button>bad</button>
      <button>reset stats</button>
      <div>good {store.getState().good}</div>
      <div>neutral</div>
      <div>bad</div>
    </div>
  )
}

const renderApp = () => {
  ReactDOM.render(<App />, document.getElementById('root'))
}

renderApp() // <-- sovelluksen ensimmäinen renderöinti
store.subscribe(renderApp) // <-- kuuntelee storen muutoksia.
